import Header from "../Header/Header";
import Main from "../Main/Main";
import Footer from "../Footer/Footer";
import ImagePopup from "../ImagePopup/ImagePopup";
import CurrentUserContext from "../../contexts/CurrentUserContext";
import EditProfilePopup from "../EditProfilePopup/EditProfilePopup";
import EditAvatarPopup from "../EditAvatarPopup/EditAvatarPopup";
import AddPlacePopup from "../AddPlacePopup/AddPlacePopup";
import api from "../../utils/api";
import React, { useEffect } from "react";

function App() {
  const [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = React.useState(false);
  const [isEditProfilePopupOpen, setEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setAddPlacePopupOpen] = React.useState(false);
  const [selectedCard, setselectedCard] = React.useState(null);
  const [currentUser, setCurrentUser] = React.useState({});
  const [cards, setCards] = React.useState([]);
  const loader = document.querySelector('.popup__form-btn')

  function handleEditAvatarClick() {
    setEditAvatarPopupOpen(true);
  }

  function handleEditProfileClick() {
    setEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setAddPlacePopupOpen(true);
  }

  function handleCardClick(card) {
    setselectedCard(card);
  }

  function handlecloseAllPopups() {
    setEditAvatarPopupOpen(false);
    setEditProfilePopupOpen(false);
    setAddPlacePopupOpen(false);
    setselectedCard(null);
  }

  function loadingHandler(isLoading) {
    if (isLoading) {
      console.log();
    } else if (!isLoading) {
      console.log('load complete');
    }
  }

  function handleUpdateUser(user) {
    api
      .editUserInfo(user)
      .then(data => {
        setCurrentUser(data)
        handlecloseAllPopups()
      })
      .catch((err) => console.log(err))
  }

  function onUpdateAvatar(avatarLink) {
    loadingHandler(true)
    api
      .editAvatar(avatarLink)
      .then(data => {
        setCurrentUser(data)
        handlecloseAllPopups()
      })
      .catch((err) => console.log(err))
      .finally(loadingHandler(false))
  }

  function handleAddPlace(place) {
    api
      .addCard(place)
      .then((newCard) => {
        setCards([newCard, ...cards])
        handlecloseAllPopups()
      })
      .catch((err) => console.log(err))
  }

  useEffect(() => {
    api
      .getUserInfo()
      .then(setCurrentUser)
      .catch((err) => console.log(err));
  }, [])

  useEffect(() => {
    api
      .getInitialCards()
      .then((data) => {
        setCards(data);
      })
      .catch((err) => console.log(err));
  }, []);

  function handleCardLike(card) {
    const isLiked = card.likes.some((i) => i._id === currentUser._id);

    api
      .changeLikeCardStatus(card._id, !isLiked)
      .then((newCard) => {
        setCards((state) =>
          state.map((c) => (c._id === card._id ? newCard : c))
        );
      })
      .catch((err) => console.log(err));
  }

  function handleCardDelete(card) {
    api.deleteCard(card._id)
      .then(() => {
        const newCards = cards.filter((newCard) => newCard !== card)
        setCards(newCards);
    })
    .catch((err) => console.log(err));
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="page">
        <Header />

        <EditAvatarPopup
        isOpen={isEditAvatarPopupOpen}
        onClose={handlecloseAllPopups}
        onUpdateAvatar={onUpdateAvatar}>
        </EditAvatarPopup>

        <EditProfilePopup
          onUpdateUser={handleUpdateUser}
          isOpen={isEditProfilePopupOpen}
          onClose={handlecloseAllPopups}
        ></EditProfilePopup>

        <AddPlacePopup
         isOpen={isAddPlacePopupOpen}
         onClose={handlecloseAllPopups}
         onAddPlace={handleAddPlace}>
        </AddPlacePopup>

        <ImagePopup card={selectedCard} onClose={handlecloseAllPopups} />

        <Main
          onEditAvatar={handleEditAvatarClick}
          onEditProfile={handleEditProfileClick}
          onAddPlace={handleAddPlaceClick}
          onCardClick={handleCardClick}
          cards={cards}
          handleCardLike={handleCardLike}
          handleCardDelete={handleCardDelete}
        />

        <Footer />
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
