import React, { useEffect }  from 'react';
import api from '../../utils/Api';
import Card from '../Card/Card';

function Main({onEditAvatar, onEditProfile, onAddPlace}) {
    const [userInfo, setUserInfo] = React.useState([]);
    const [cards, setCards] = React.useState([]);

    useEffect(() => {
        api.getUserInfo()
        .then(setUserInfo)
        .catch(err => console.log(err))
    }, [])

    useEffect(() => {
        api.getInitialCards()
        .then(data => {
            console.log(data);
            setCards(data.map(item => ({
                src: item.link,
                name: item.name,
                likes: item.likes.length
            })))
        })
        .catch(err => console.log(err))
    }, [])


    return (
        <main className="content">

        {/* <div className="popup popup_delete_card">
            <div className="popup__container">
                <button type="button" className="popup__close-btn"><img className="popup__close-image" src={closeBtn} alt="закрыть"/></button>
                <div className="popup__card-content">
                    <form noValidate name="delete-card" className="popup__form popup__delete-card" action="send">
                        <h2 className="popup__form-title">Вы уверены?</h2>
                        <button type="submit" className="popup__form-btn">Да</button>
                    </form>
                </div>
            </div>
        </div> */}

        <section className="profile">
            <div className="profile__avatar-container" onClick={onEditAvatar}>
                <img className="profile__avatar" src={userInfo.avatar} alt="аватар"/>
            </div>
            <div className="profile__info">
                <h1 className="profile__title">{userInfo.name}</h1>
                <button type="button" className="profile__edit-button" onClick={onEditProfile}></button>
                <p className="profile__subtitle">{userInfo.about}</p>
            </div>
            <button type="button" className="profile__add-place-button" onClick={onAddPlace}></button>
        </section>

        <ul className="elements">
            {cards.map((card) => {
                return <Card{...card} />
            })}
        </ul>

      </main>
    )
}

export default Main;
