import closeBtn from '../../images/close.svg'

function PopupWithForm(props) {
    return (
        <div className={`popup popup_type_${props.name} ${props.isOpen? 'popup_opened' : ''}`}>
            <div className="popup__container">
                <button type="button" id="close-profile" className="popup__close-btn"><img className="popup__close-image" src={closeBtn} alt="закрыть"/></button>
                <form noValidate id="edit-profile-form" name={props.name} className="popup__form" action="send">
                    <h2 className="popup__form-title">{props.title}</h2>
                    {props.children}
                    {/* <input name="name" minLength="2" maxLength="40" required id="title" className="popup__form-input" type="text"/>
                    <span className="popup__form-input-error title-error"></span>
                    <input name="about" minLength="2" maxLength="200" required id="subtitle" className="popup__form-input" type="text"/>
                    <span className="popup__form-input-error subtitle-error"></span> */}
                    <button type="submit" className="popup__form-btn">Сохранить</button>
                </form>
            </div>
        </div>
    )
}

export default PopupWithForm;
