import Header from "../Header/Header";
import Main from "../Main/Main";
import Footer from "../Footer/Footer";
import PopupWithForm from "../PopupWithForm/PopupWithForm";
import ImagePopup from "../ImagePopup/ImagePopup";
import CurrentUserContext from "../../contexts/CurrentUserContext";
import EditProfilePopup from "../EditProfilePopup/EditProfilePopup";
import EditAvatarPopup from "../EditAvatarPopup/EditAvatarPopup";
import api from "../../utils/api";
import React, { useEffect } from "react";

function App() {
  const [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = React.useState(false);
  const [isEditProfilePopupOpen, setEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setAddPlacePopupOpen] = React.useState(false);
  const [selectedCard, setselectedCard] = React.useState(null);
  const [currentUser, setCurrentUser] = React.useState({});

  function handleEditAvatarClick() {
    setEditAvatarPopupOpen(true);
  }

  function handleEditProfileClick() {
    setEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setAddPlacePopupOpen(true);
  }

  function handleCardClick(card) {
    setselectedCard(card);
  }

  function handlecloseAllPopups() {
    setEditAvatarPopupOpen(false);
    setEditProfilePopupOpen(false);
    setAddPlacePopupOpen(false);
    setselectedCard(null);
  }

  function handleUpdateUser(user) {
    api
      .editUserInfo(user)
      .then(setCurrentUser)
      .then(handlecloseAllPopups)
      .catch((err) => console.log(err));
  }

  function onUpdateAvatar(avatarLink) {
    console.log(avatarLink);
    // api
    //   .editAvatar(avatarLink)

  }

  useEffect(() => {
    api
      .getUserInfo()
      .then(setCurrentUser)
      .catch((err) => console.log(err));
  }, [])

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="page">
        <Header />

        <EditAvatarPopup
        isOpen={isEditAvatarPopupOpen}
        onClose={handlecloseAllPopups}
        onUpdateAvatar={onUpdateAvatar}>
        </EditAvatarPopup>

        <EditProfilePopup
          onUpdateUser={handleUpdateUser}
          isOpen={isEditProfilePopupOpen}
          onClose={handlecloseAllPopups}
        ></EditProfilePopup>

        <PopupWithForm
          isOpen={isAddPlacePopupOpen}
          onClose={handlecloseAllPopups}
          name="add-place"
          title="Новое место"
        >
          <input
            name="name"
            minLength="2"
            maxLength="30"
            required
            placeholder="Название"
            id="name-input"
            className="popup__form-input"
            type="text"
          />
          <span className="popup__form-input-error name-input-error"></span>
          <input
            name="link"
            required
            placeholder="Ссылка на картинку"
            id="image-input"
            className="popup__form-input"
            type="url"
          />
          <span className="popup__form-input-error image-input-error"></span>
        </PopupWithForm>

        <ImagePopup card={selectedCard} onClose={handlecloseAllPopups} />

        <Main
          onEditAvatar={handleEditAvatarClick}
          onEditProfile={handleEditProfileClick}
          onAddPlace={handleAddPlaceClick}
          onCardClick={handleCardClick}
        />

        <Footer />
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
