import React, { useEffect } from "react";
import CurrentUserContext from "../../contexts/CurrentUserContext";
import api from "../../utils/api";
import Card from "../Card/Card";

function Main({ onEditAvatar, onEditProfile, onAddPlace, onCardClick }) {
  const [cards, setCards] = React.useState([]);
  const userInfo = React.useContext(CurrentUserContext);
  const currentUser = React.useContext(CurrentUserContext);

  useEffect(() => {
    api
      .getInitialCards()
      .then((data) => {
        setCards(
          data.map((item) => ({
            link: item.link,
            name: item.name,
            likes: item.likes,
            key: item._id,
            owner: item.owner,
            _id: item._id
          }))
        );
      })
      .catch((err) => console.log(err));
  }, []);

  function handleCardLike(card) {
    const isLiked = card.likes.some((i) => i._id === currentUser._id);
    console.log(card);

    api.likeCard(card._id, !isLiked).then((newCard) => {
      setCards((state) => state.map((c) => (c._id === card._id ? newCard : c)));
    });
  };

  return (
    <main className="content">
      <section className="profile">
        <div className="profile__avatar-container" onClick={onEditAvatar}>
          <img className="profile__avatar" src={userInfo.avatar} alt="аватар" />
        </div>
        <div className="profile__info">
          <h1 className="profile__title">{userInfo.name}</h1>
          <button
            type="button"
            className="profile__edit-button"
            onClick={onEditProfile}
          ></button>
          <p className="profile__subtitle">{userInfo.about}</p>
        </div>
        <button
          type="button"
          className="profile__add-place-button"
          onClick={onAddPlace}
        ></button>
      </section>

      <ul className="elements">
        {cards.map((card) => {
          return <Card {...card} card={card} key={card._id} onCardClick={onCardClick} onCardLike={handleCardLike} />;
        })}
      </ul>
    </main>
  );
}

export default Main;
