import Header from '../Header/Header';
import Main from '../Main/Main';
import Footer from '../Footer/Footer';
import PopupWithForm from '../PopupWithForm/PopupWithForm';
import ImagePopup from '../ImagePopup/ImagePopup'; 
import React from 'react';

function App() {
  const [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = React.useState(false);
  const [isEditProfilePopupOpen, setEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setAddPlacePopupOpen] = React.useState(false);
  const [selectedCard, setselectedCard] = React.useState(false);

  function handleEditAvatarClick() {
    setEditAvatarPopupOpen(true);
  }

  function handleEditProfileClick() {
    setEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setAddPlacePopupOpen(true);
  }

  function handleCardClick(card) {
    // console.log(card);
    setselectedCard(card);
  }

  console.log(selectedCard);

  function handlecloseAllPopups() {
    setEditAvatarPopupOpen(false);
    setEditProfilePopupOpen(false);
    setAddPlacePopupOpen(false);
  }

  return (
    <div className="page">
      <Header />

      <PopupWithForm 
      isOpen={isEditAvatarPopupOpen} 
      onClose={handlecloseAllPopups}
      name='edit-avatar'
      title='Обновить аватар'>
        <input name="link" required placeholder="Ссылка на картинку" id="edit-input" className="popup__form-input" type="url"/>
        <span className="popup__form-input-error edit-input-error"></span>
      </PopupWithForm>


      <PopupWithForm 
      isOpen={isEditProfilePopupOpen} 
      onClose={handlecloseAllPopups}
      name='edit-profile'
      title='Редактировать профиль'>
        <input name="name" minLength="2" maxLength="40" required id="title" className="popup__form-input" type="text"/>
        <span className="popup__form-input-error title-error"></span>
        <input name="about" minLength="2" maxLength="200" required id="subtitle" className="popup__form-input" type="text"/>
        <span className="popup__form-input-error subtitle-error"></span>
      </PopupWithForm>


      <PopupWithForm 
      isOpen={isAddPlacePopupOpen} 
      onClose={handlecloseAllPopups}
      name='add-place'
      title='Новое место'>
        <input name="name" minLength="2" maxLength="30" required placeholder="Название" id="name-input" className="popup__form-input" type="text"/>
        <span className="popup__form-input-error name-input-error"></span>
        <input name="link" required placeholder="Ссылка на картинку" id="image-input" className="popup__form-input" type="url"/>
        <span className="popup__form-input-error image-input-error"></span>
      </PopupWithForm>

      <ImagePopup card={selectedCard}/>

      <Main onEditAvatar={handleEditAvatarClick} onEditProfile={handleEditProfileClick} onAddPlace={handleAddPlaceClick} onCardClick={handleCardClick}/>

      <Footer />
    </div>
  );
}

export default App;
